package features;

public class GreatestOf3Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 9;
		float b=8.8f;
		int c = 2;
		if(a>b && a>c)
			System.out.println("a is greatest");
		else if(b>a && b>c)
			System.out.println("b is greatest");
		else
			System.out.println("c is greatest");
	}

}
